import unittest
from custom_sort import custom_sort


class TestCustomSort(unittest.TestCase):
    def test_custom_sort(self):
        self.assertEqual(custom_sort("edc", "abcdefzyx"), "edcabfxyz")
        self.assertEqual(custom_sort("fby", "abcdefzyx"), "fbyacdexz")
        self.assertEqual(custom_sort("", "abcdefzyx"), "abcdefxyz")
        self.assertEqual(custom_sort("", ""), "")
        self.assertEqual(custom_sort('cteqh', 'xnjanztmhg'), "thagjmnnxz")
        self.assertEqual(custom_sort('jv', 'cxkafinfiuhnnaracsztbrcwaifwattzavwohoqskauififucq'), "vaaaaaaabccccfffffhhiiiiikknnnooqqrrsstttuuuwwwxzz")
        self.assertEqual(custom_sort('xtz', 'uhgpqydzwtsemucfidpvrvormgfjefpqeprvmyiazqokmnhdwvyglngcsriw'), "tzzaccdddeeefffgggghhiiijklmmmmnnooppppqqqrrrrssuuvvvvwwwyyy")
        self.assertEqual(custom_sort('eyuti', 'iuanyojxqbmihzyvjunb'), "yyuuiiabbhjjmnnoqvxz")
        self.assertEqual(custom_sort('ndgi', 'eqfbnjbpbisjoakfnncpaphvvzxsodaw'), "nnndiaaabbbceffhjjkoopppqssvvwxz")
        self.assertEqual(custom_sort('ymd', 'duoisjfobwlztxsaymzjwvpofouyijglwgvogpt'), "yymdabffgggiijjjlloooooppssttuuvvwwwxzz")
        self.assertEqual(custom_sort('wpkmv', 'crcasndkumqcckmqxcpagkjveovcmnmzvbkixeduqohkutbelm'), "pkkkkkmmmmmvvvaabbccccccddeeeghijlnnooqqqrstuuuxxz")
        self.assertEqual(custom_sort('qdmbfc', 'mruwsotaydpzeezpgmmcrhbwsihrbzryqheigapvaehnyjgvbvqrdxtqkczujzqngpdmrduffcokcnfcqqmqtwbquzssalydifip'), "qqqqqqqqdddddmmmmmbbbbffffcccccaaaaeeeegggghhhhiiiijjkklnnnooppppprrrrrrsssstttuuuuvvvwwwxyyyyzzzzzz")
        self.assertEqual(custom_sort('iupmz', 'ixavjrmbwacgwvxcrauvcfcizfoxxabdyossqvzubqnpdzphbpurikqzqigjdrjsycpjlpstokvnijpcchcgtdfvqkltbdqidptvbsmwxeuqxyvcyzfiksnrrzbpmxdlhzieocmabqszqanfwjqmjb'), "iiiiiiiiuuuuppppppppmmmmmzzzzzzzzaaaaaabbbbbbbbbccccccccccdddddddeefffffggghhhjjjjjjjkkkklllnnnnooooqqqqqqqqqqrrrrrrsssssssttttvvvvvvvvwwwwxxxxxxxyyyy")
        self.assertEqual(custom_sort('cjhfnu', 'uokmxyzaisghuqmxxhnntlhhasjwfkjutdoumlkwfusscjspauroihkxmsvamxuqronfszjmgnqjlwdkvndslsykrojvrienrhtrvtrmacuneouorbjceadhskonhbnghctqbqkgzqwuaegumugggilhjbzqcpgbpkfxlbeyinfdrzkvesxjnmyvrwbftuymtzrzzzlm'), "cccccjjjjjjjjjhhhhhhhhhhffffffnnnnnnnnnnnuuuuuuuuuuuuuaaaaaaabbbbbbbdddddeeeeeegggggggggiiiiikkkkkkkkkklllllllmmmmmmmmmmmoooooooopppqqqqqqqrrrrrrrrrrrssssssssssstttttttvvvvvvwwwwwxxxxxxxyyyyyzzzzzzzzz")
        self.assertEqual(custom_sort('novctmix', ''), "")
        self.assertEqual(custom_sort('thvuybo', ''), "")
        self.assertEqual(custom_sort('pgjiyhkor', ''), "")
        self.assertEqual(custom_sort('jxrqklbgut', ''), "")
        self.assertEqual(custom_sort('vupdsfgrxkob', ''), "")
        self.assertEqual(custom_sort('opdzsivgarylwut', ''), "")

if __name__ == '__main__':
    unittest.main()
